mapper.insert:
insert into work_room (id, name, status, 
  work_picture, text, create_at, 
  update_at, create_by, update_by
  )
values (#{id,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR}, #{status,jdbcType=BIGINT}, 
  #{workPicture,jdbcType=VARCHAR}, #{text,jdbcType=VARCHAR}, #{createAt,jdbcType=BIGINT}, 
  #{updateAt,jdbcType=BIGINT}, #{createBy,jdbcType=BIGINT}, #{updateBy,jdbcType=BIGINT}
  )

mapper.select:
        select * from work_room where 1=1

        
            and name like concat('%','${name}','%' )
        
        
            and status =#{status}
