mapper.insert:
insert into studio (id, name, status, 
  picture, content, create_at, 
  create_by, update_at, update_by
  )
values (#{id,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR}, #{status,jdbcType=INTEGER}, 
  #{picture,jdbcType=VARCHAR}, #{content,jdbcType=VARCHAR}, #{createAt,jdbcType=BIGINT},
  #{createBy,jdbcType=VARCHAR}, #{updateAt,jdbcType=BIGINT}, #{updateBy,jdbcType=VARCHAR}
  )

mapper.select:
select * from studio


    name like CONCAT('%','${name}','%')


    and status = #{status,jdbcType=INTEGER}
